import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Search = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [stockOptions, setStockOptions] = useState([]);

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (searchTerm) {
        // Your API call here
      }
    }, 1000); // Will invoke the API call 1 second after the user stops typing
  
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  useEffect(() => {
    const apiKey = 'BUY57XDDM8DE6IPJ'; // Your Alpha Vantage API Key

    if (searchTerm) {
      const url = `https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${searchTerm}&apikey=${apiKey}`;

      axios.get(url)
        .then(response => {
          // Extracting the best matches from Alpha Vantage response
          if (response.data && response.data.bestMatches) {
            setStockOptions(response.data.bestMatches);
          } else {
            setStockOptions([]);
          }
        })
        .catch(error => console.error("Error fetching data: ", error));
    } else {
      setStockOptions([]);
    }
  }, [searchTerm]);

  return (
    <div>
      <form onSubmit={(e) => e.preventDefault()}>
        <input 
          type="text" 
          value={searchTerm} 
          onChange={(e) => setSearchTerm(e.target.value)} 
          placeholder="Search for a stock" 
        />
      </form>
      {stockOptions.length > 0 && (
        <ul>
          {stockOptions.map((stock, index) => (
            <li key={index}>
              {stock['2. name']} ({stock['1. symbol']})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Search;
