import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Ticker.css";

const Search = ({ navigateTo, setSelectedStock }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [stockOptions, setStockOptions] = useState([]);
  const [tickerData, setTickerData] = useState([]); // State for ticker data
  const [previousPrices, setPreviousPrices] = useState({}); // State to store previous prices

  useEffect(() => {
    // Fetch ticker data
    const fetchTickerData = async () => {
      try {
        const apiKey = "pk_73eb7c2f76b14c34afa8f99215f526e6";
        const symbols = "AAPL,MSFT,AMZN,GOOGL,FB,BABA,TSLA,V,JPM,JNJ"; // Replace with actual stock symbols
        const url = `https://cloud.iexapis.com/stable/stock/market/batch?symbols=${symbols}&types=quote&token=${apiKey}`;

        const response = await axios.get(url);
        const formattedData = Object.keys(response.data).map((key) => {
          const quote = response.data[key].quote;
          const previousPrice =
            previousPrices[quote.symbol] || quote.latestPrice;
          let trend = "neutral";
          if (quote.latestPrice > previousPrice) {
            trend = "up";
          } else if (quote.latestPrice < previousPrice) {
            trend = "down";
          }
          return {
            symbol: quote.symbol,
            price: quote.latestPrice,
            trend: trend,
          };
        });

        // Update previous prices
        const newPreviousPrices = formattedData.reduce((acc, curr) => {
          acc[curr.symbol] = curr.price;
          return acc;
        }, {});
        setPreviousPrices(newPreviousPrices);

        setTickerData(formattedData); // Update ticker data
      } catch (error) {
        console.error("Error fetching ticker data: ", error);
      }
    };

    fetchTickerData();
    const interval = setInterval(fetchTickerData, 60000); // Update every minute

    return () => clearInterval(interval);
  }, [previousPrices]); // Add previousPrices to the dependency array

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (searchTerm) {
        const apiKey = "pk_73eb7c2f76b14c34afa8f99215f526e6";
        const url = `https://cloud.iexapis.com/stable/search/${searchTerm}?token=${apiKey}`;

        axios
          .get(url)
          .then((response) => {
            setStockOptions(response.data);
          })
          .catch((error) => {
            console.error("Error fetching data: ", error);
            setStockOptions([]);
          });
      } else {
        setStockOptions([]);
      }
    }, 1000);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  const handleStockClick = (symbol) => {
    setSelectedStock(symbol);
    navigateTo("stock");
  };

  return (
    <div>
      <div className="ticker-wrap">
        <div className="ticker">
          {tickerData.map((item, index) => (
            <div className="ticker-item" key={index}>
              {item.symbol}: {item.price}
              {item.trend === "up" ? " ↑" : item.trend === "down" ? " ↓" : " -"}
            </div>
          ))}
        </div>
      </div>
      <form onSubmit={(e) => e.preventDefault()}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a stock"
        />
      </form>
      {stockOptions.length > 0 && (
        <ul>
          {stockOptions.map((stock, index) => (
            <li key={index} onClick={() => handleStockClick(stock.symbol)}>
              {stock.name} ({stock.symbol})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Search;
