import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale } from 'chart.js';
import 'chartjs-adapter-moment';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const timePeriods = ['1d', '5d', '1m', '6m', '1y', '5y', 'max']; // Define time periods
const analysisOptions = ['Option 1', 'Option 2', 'Option 3']; // Placeholder analysis options

const Stock = ({ selectedStock }) => {
  const [stockData, setStockData] = useState({
    labels: [],
    datasets: [
      {
        label: `${selectedStock} Stock Price`,
        data: [],
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      }
    ]
  });
  const [stockInfo, setStockInfo] = useState({ high: 0, low: 0, current: 0 });
  const [selectedTimePeriod, setSelectedTimePeriod] = useState('1m');

  useEffect(() => {
    const apiKey = 'pk_73eb7c2f76b14c34afa8f99215f526e6';
    const url = `https://cloud.iexapis.com/stable/stock/${selectedStock}/chart/${selectedTimePeriod}?token=${apiKey}`;

    axios.get(url)
      .then(response => {
        if (response.data) {
          const chartData = parseChartData(response.data);
          setStockData(chartData);
          const lastDayData = response.data[response.data.length - 1];
          setStockInfo({
            high: lastDayData.high,
            low: lastDayData.low,
            current: lastDayData.close
          });
        }
      })
      .catch(error => console.error("Error fetching stock time series data: ", error));
  }, [selectedStock, selectedTimePeriod]);

  const parseChartData = (data) => {
    const chartData = {
      labels: [],
      datasets: [{
        label: `${selectedStock} Stock Price`,
        data: [],
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      }]
    };

    data.forEach(dayData => {
      chartData.labels.push(dayData.date);
      chartData.datasets[0].data.push(dayData.close);
    });

    return chartData;
  };

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day'
        }
      },
      y: {
        beginAtZero: true
      }
    },
    responsive: true,
    maintainAspectRatio: false
  };

  const handleAnalysisOptionClick = (option) => {
    console.log(`Analysis option ${option} selected`); // Placeholder functionality
    // Implement the functionality for each analysis option here
  };

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: '20px' }}>
      <div style={{ width: '600px', height: '300px' }}>
        <h2 style={{ textAlign: 'center' }}>{selectedStock} Stock Chart</h2>
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '10px' }}>
          {timePeriods.map(period => (
            <button key={period} onClick={() => setSelectedTimePeriod(period)}>{period.toUpperCase()}</button>
          ))}
        </div>
        {stockData.datasets[0].data.length > 0 && (
          <Line data={stockData} options={options} />
        )}
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          {analysisOptions.map((option, index) => (
            <button key={index} onClick={() => handleAnalysisOptionClick(option)}>{option}</button>
          ))}
        </div>
      </div>
      <div style={{ marginLeft: '20px' }}>
        {/* Stock info as before */}
      </div>
    </div>
  );
};

export default Stock;
