// FavoriteStock.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import "./FavoriteStock.css"; // Create this CSS file for styling

const FavoriteStock = ({ symbol }) => {
  const [stockData, setStockData] = useState({
    high: 0,
    low: 0,
    current: 0,
    chartData: {},
  });

  useEffect(() => {
    const fetchStockData = async () => {
      const apiKey = "pk_302c7bd6ee464d738f364961b88569ee"; // Use your API key
      const chartUrl = `https://cloud.iexapis.com/stable/stock/${symbol}/chart/1m?token=${apiKey}`;
      const quoteUrl = `https://cloud.iexapis.com/stable/stock/${symbol}/quote?token=${apiKey}`;

      try {
        // Fetching stock chart data
        const chartResponse = await axios.get(chartUrl);
        const chartData =
          chartResponse.data?.map((data) => ({
            x: data.date,
            y: data.close,
          })) || [];

        // Fetching stock quote data
        const quoteResponse = await axios.get(quoteUrl);
        const { high, low, latestPrice: current } = quoteResponse.data || {};

        // Update state
        setStockData({
          high: high || 0,
          low: low || 0,
          current: current || 0,
          chartData: {
            labels: chartData.map((data) => data.x),
            datasets: [
              {
                label: `${symbol} Price`,
                data: chartData.map((data) => data.y),
                borderColor: "rgba(75,192,192,1)",
                backgroundColor: "rgba(75,192,192,0.5)",
                fill: false,
              },
            ],
          },
        });
      } catch (error) {
        console.error("Error fetching stock data: ", error);
      }
    };

    fetchStockData();
  }, [symbol]);

  return (
    <div className="favorite-stock-container">
      <h3>{symbol}</h3>
      <Line data={stockData.chartData} /* Options and other props */ />
      <p>High: {stockData.high}</p>
      <p>Low: {stockData.low}</p>
      <p>Current: {stockData.current}</p>
    </div>
  );
};

export default FavoriteStock;
