import React, { useState, useEffect } from "react";
import axios from "axios";
import "./styles.css";

const Search = ({ navigateTo, setSelectedStock }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [stockOptions, setStockOptions] = useState([]);
  const [tickerData, setTickerData] = useState([]); // State for ticker data

  useEffect(() => {
    const fetchTickerData = async () => {
      try {
        const apiKey = "pk_8135f55c77024901816d7722ddd20cbf";

        const symbols =
          "AAPL,MSFT,AMZN,GOOGL,FB,BABA,TSLA,V,JPM,JNJ,BRK.A,XOM,BAC,PG,WMT,DIS,VZ,PFE,CVX,KO,PEP,CSCO,MRK,INTC,CMCSA";

        const batchUrl = `https://cloud.iexapis.com/stable/stock/market/batch?symbols=${symbols}&types=quote,previous&token=${apiKey}`;

        const response = await axios.get(batchUrl);
        const formattedData = Object.keys(response.data).map((key) => {
          const quote = response.data[key].quote;
          const previousClose = response.data[key].previous.close;
          let trend = "neutral";
          if (quote.latestPrice > previousClose) {
            trend = "up";
          } else if (quote.latestPrice < previousClose) {
            trend = "down";
          }
          return {
            symbol: quote.symbol,
            price: quote.latestPrice,
            trend: trend,
          };
        });

        setTickerData([...formattedData, ...formattedData]);
      } catch (error) {
        console.error("Error fetching ticker data: ", error);
      }
    };

    fetchTickerData();
    const interval = setInterval(fetchTickerData, 60000); // Update every minute
    return () => clearInterval(interval);
  }, []);

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (searchTerm) {
        const apiKey = "pk_8135f55c77024901816d7722ddd20cbf";
        const url = `https://cloud.iexapis.com/stable/search/${searchTerm}?token=${apiKey}`;

        axios
          .get(url)
          .then((response) => {
            setStockOptions(response.data);
          })
          .catch((error) => {
            console.error("Error fetching data: ", error);
            setStockOptions([]);
          });
      } else {
        setStockOptions([]);
      }
    }, 1000);

    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);

  const handleStockClick = (symbol) => {
    setSelectedStock(symbol);
    navigateTo("stock");
  };

  return (
    <div className="search-container">
      <div className="ticker-wrap">
        <div className="ticker">
          {tickerData.map((item, index) => (
            <div className="ticker-item" key={index}>
              {item.symbol}: {item.price}
              {item.trend === "up" ? " ↑" : item.trend === "down" ? " ↓" : " -"}
            </div>
          ))}
        </div>
      </div>
      <h2 className="title">Stock Analyzer</h2>
      <div className="search-bar">
        <form onSubmit={(e) => e.preventDefault()} className="search-form">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search for a stock"
            className="search-input"
          />
        </form>
      </div>
      {stockOptions.length > 0 && (
        <ul className="stock-list">
          {stockOptions.map((stock, index) => (
            <li
              key={index}
              onClick={() => handleStockClick(stock.symbol)}
              className="stock-item"
            >
              {stock.name} ({stock.symbol})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Search;
