import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import "./Stock.css";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from "chart.js";
import "chartjs-adapter-moment";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const timePeriods = ["1d", "5d", "1m", "6m", "1y", "5y", "max"];
const analysisOptions = [
  "Simple Moving Average",
  "Exponential Moving Average",
  "Relative Strength Index",
  "Bollinger Bands",
  "MACD",
  "Stochastic Oscillator",
  "Fibonacci Retracement",
];

const Stock = ({ selectedStock }) => {
  const [stockInfo, setStockInfo] = useState({ high: 0, low: 0, current: 0 });
  const [selectedTimePeriod, setSelectedTimePeriod] = useState("1m");
  const [tickerData, setTickerData] = useState([]); // State for ticker data
  const [stockData, setStockData] = useState({
    labels: [],
    datasets: [
      {
        label: `${selectedStock} Stock Price`,
        data: [],
        borderColor: "rgba(75,192,192,1)",
        backgroundColor: "rgba(75,192,192,0.5)",
        fill: false,
        pointRadius: 3, // Adjust point size
        pointBackgroundColor: "black", // Black dots
      },
    ],
  });

  const calculatePrediction = async () => {
    let bullish = 0, bearish = 0, neutral = 0;
    for (const analysisType of analysisOptions) {
      const trend = await handleAnalysisOptionClick(analysisType);
      if (trend === 'bullish') bullish++;
      else if (trend === 'bearish') bearish++;
      else neutral++;
    }

    // Calculate and display overall prediction
    if (bullish > bearish) alert('Overall Prediction: Bullish');
    else if (bearish > bullish) alert('Overall Prediction: Bearish');
    else alert('Overall Prediction: Neutral');
  };
  
  useEffect(() => {
    const fetchTickerData = async () => {
      const apiKey = "pk_73eb7c2f76b14c34afa8f99215f526e6";
      const symbols =
        "AAPL,MSFT,AMZN,GOOGL,FB,BABA,TSLA,V,JPM,JNJ,BRK.A,XOM,BAC,PG,WMT,DIS,VZ,PFE,CVX,KO,PEP,CSCO,MRK,INTC,CMCSA";
      const url = `https://cloud.iexapis.com/stable/stock/market/batch?symbols=${symbols}&types=quote,previous&token=${apiKey}`;

      try {
        const response = await axios.get(url);
        // Process and set the ticker data
        // ...
      } catch (error) {
        console.error("Error fetching ticker data: ", error);
      }
    };

    fetchTickerData();
  }, []);
  useEffect(() => {
    const apiKey = "pk_73eb7c2f76b14c34afa8f99215f526e6";
    const url = `https://cloud.iexapis.com/stable/stock/${selectedStock}/chart/${selectedTimePeriod}?token=${apiKey}`;

    axios
      .get(url)
      .then((response) => {
        if (response.data) {
          const chartData = parseChartData(response.data);
          setStockData(chartData);
          const lastDayData = response.data[response.data.length - 1];
          setStockInfo({
            high: lastDayData.high,
            low: lastDayData.low,
            current: lastDayData.close,
          });
        }
      })
      .catch((error) =>
        console.error("Error fetching stock time series data: ", error)
      );
  }, [selectedStock, selectedTimePeriod]);

  const parseChartData = (data) => {
    const chartData = {
      labels: [],
      datasets: [
        {
          label: `${selectedStock} Stock Price`,
          data: [],
          borderColor: "rgba(75,192,192,1)",
          fill: false,
        },
      ],
    };

    data.forEach((dayData) => {
      chartData.labels.push(dayData.date);
      chartData.datasets[0].data.push(dayData.close);
    });

    return chartData;
  };

  const options = {
    scales: {
      x: {
        type: "time",
        time: {
          unit: "day",
        },
      },
      y: {
        beginAtZero: true,
      },
    },
    responsive: true,
    maintainAspectRatio: false,
  };

  const handleAnalysisOptionClick = async (analysisType) => {
    try {
      const analysisUrlPart = analysisType.toLowerCase().replace(/\s+/g, '-');
      const url = `http://localhost:3001/${analysisUrlPart}/${selectedStock}/${selectedTimePeriod}`;
      
      const response = await axios.get(url);
      return response.data.trend; // Assuming the API response structure has a 'trend' field
    } catch (error) {
      console.error(`Error performing ${analysisType} analysis:`, error);
      return 'error'; // Return 'error' or a similar flag in case of an error
    }
  };

  return (
    <div className="stock-container">
      <div className="stock-ticker">
        {tickerData.map((item, index) => (
          <div key={index} className="ticker-item">
            {item.symbol}: {item.price}
            {item.trend === "up" ? " ↑" : item.trend === "down" ? " ↓" : " -"}
          </div>
        ))}
      </div>
      <h1 className="stock-title">Stock Analyzer</h1>

      <div style={{ marginBottom: "20px" }}>
        {timePeriods.map((period) => (
          <button key={period} onClick={() => setSelectedTimePeriod(period)}>
            {period.toUpperCase()}
          </button>
        ))}
      </div>

      <div style={{ display: "flex", justifyContent: "center", alignItems: "flex-start" }}>
        <div style={{ width: "600px", height: "300px" }}>
          <h2 style={{ textAlign: "center" }}>{selectedStock} Stock Chart</h2>
          {stockData.datasets[0].data.length > 0 && (
            <Line data={stockData} options={options} />
          )}
        </div>
        <div style={{ marginLeft: "20px" }}>
          <p><strong>High:</strong> {stockInfo.high}</p>
          <p><strong>Low:</strong> {stockInfo.low}</p>
          <p><strong>Current:</strong> {stockInfo.current}</p>
          <p><strong>Bullish:</strong> Buy Stock</p>
          <p><strong>Bearish:</strong> Sell Stock</p>
          <p><strong>Neutral:</strong> Keep Stock</p>
          <p><strong>Verdict:</strong> Keep Stock</p>
        </div>
      </div>
      <button onClick={calculatePrediction}>Calculate Prediction</button>
      <div style={{ display: "flex", justifyContent: "center", padding: "80px" }}>
        {analysisOptions.map((option, index) => (
          <button key={index} onClick={() => handleAnalysisOptionClick(option)}>
            {option}
          </button>
        ))}
      </div>
    </div>
  );
};

export default Stock;
