import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

const Stock = ({ selectedStock }) => {
  const [stockData, setStockData] = useState({});

  useEffect(() => {
    const apiKey = 'pk_73eb7c2f76b14c34afa8f99215f526e6'; // Replace with your IEX Cloud API Key
    // IEX Cloud's Historical Prices endpoint example
    const url = `https://cloud.iexapis.com/stable/stock/${selectedStock}/chart/1m?token=${apiKey}`;

    axios.get(url)
      .then(response => {
        if (response.data) {
          const chartData = parseChartData(response.data);
          setStockData(chartData);
        }
      })
      .catch(error => console.error("Error fetching stock time series data: ", error));
  }, [selectedStock]);

  const parseChartData = (data) => {
    // Adapt this parsing logic to match the structure of data from IEX Cloud
    const chartData = {
      labels: [],
      datasets: [{
        label: `${selectedStock} Stock Price`,
        data: [],
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      }]
    };

    data.forEach(dayData => {
      chartData.labels.push(dayData.date);
      chartData.datasets[0].data.push(dayData.close); // Assuming 'close' is part of the response
    });

    return chartData;
  };

  return (
    <div>
      <h2>{selectedStock} Stock Chart</h2>
      <Line data={stockData} />
    </div>
  );
};

export default Stock;
