import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale } from 'chart.js';
import 'chartjs-adapter-moment'; // This is important for time scale

// Register the necessary components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const Stock = ({ selectedStock }) => {
    const [stockData, setStockData] = useState({
        labels: [],
        datasets: [
          {
            label: `${selectedStock} Stock Price`,
            data: [],
            borderColor: 'rgba(75,192,192,1)',
            fill: false,
          }
        ]
      });
      
  useEffect(() => {
    const apiKey = 'pk_73eb7c2f76b14c34afa8f99215f526e6'; // Replace with your IEX Cloud API Key
    const url = `https://cloud.iexapis.com/stable/stock/${selectedStock}/chart/1m?token=${apiKey}`;

    axios.get(url)
      .then(response => {
        if (response.data) {
          const chartData = parseChartData(response.data);
          setStockData(chartData);
        }
      })
      .catch(error => console.error("Error fetching stock time series data: ", error));
  }, [selectedStock]);

  const parseChartData = (data) => {
    const chartData = {
      labels: [],
      datasets: [{
        label: `${selectedStock} Stock Price`,
        data: [],
        borderColor: 'rgba(75,192,192,1)',
        fill: false,
      }]
    };

    data.forEach(dayData => {
      chartData.labels.push(dayData.date);
      chartData.datasets[0].data.push(dayData.close);
    });

    return chartData;
  };

  const options = {
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day'
        }
      },
      y: {
        beginAtZero: true
      }
    },
    responsive: true,
    maintainAspectRatio: false
  };

  return (
    <div style={{ height: '400px', width: '600px' }}>
      <h2>{selectedStock} Stock Chart</h2>
      {stockData.datasets[0].data.length > 0 && (
        <Line data={stockData} options={options} />
      )}
    </div>
  );
};

export default Stock;
