import React, { useState } from 'react';
import axios from 'axios';

const ForgotPassword = ({ navigateTo }) => {
  const [email, setEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (newPassword !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }
    // Implement the logic to update the password
    // This requires a new backend route and logic
    alert('Password reset functionality not implemented yet');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
      <input type="password" value={newPassword} onChange={(e) => setNewPassword(e.target.value)} placeholder="New Password" required />
      <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} placeholder="Confirm Password" required />
      <button type="submit">Reset Password</button>
      <button type="button" onClick={() => navigateTo('login')}>Back to Login</button>
    </form>
  );
};

export default ForgotPassword;
