import React, { useState, useEffect } from "react";
import axios from "axios";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale,
} from "chart.js";
import "chartjs-adapter-moment";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const timePeriods = ["1d", "5d", "1m", "6m", "1y", "5y", "max"];
const analysisOptions = [
  "Simple Moving Average",
  "Exponential Moving Average",
  "Relative Strength Index",
  "Bollinger Bands",
  "MACD",
  "Stochastic Oscillator",
  "Fibonacci Retracement",
];

const Stock = ({ selectedStock }) => {
  const [stockData, setStockData] = useState({
    labels: [],
    datasets: [
      {
        label: `${selectedStock} Stock Price`,
        data: [],
        borderColor: "rgba(75,192,192,1)",
        backgroundColor: "rgba(75,192,192,0.5)",
        fill: false,
        pointRadius: 3, // Adjust point size
        pointBackgroundColor: "black", // Black dots
      },
    ],
  });
  const [stockInfo, setStockInfo] = useState({ high: 0, low: 0, current: 0 });
  const [selectedTimePeriod, setSelectedTimePeriod] = useState("1m");

  useEffect(() => {
    const apiKey = "pk_73eb7c2f76b14c34afa8f99215f526e6";
    const url = `https://cloud.iexapis.com/stable/stock/${selectedStock}/chart/${selectedTimePeriod}?token=${apiKey}`;

    axios
      .get(url)
      .then((response) => {
        if (response.data) {
          const chartData = parseChartData(response.data);
          setStockData(chartData);
          const lastDayData = response.data[response.data.length - 1];
          setStockInfo({
            high: lastDayData.high,
            low: lastDayData.low,
            current: lastDayData.close,
          });
        }
      })
      .catch((error) =>
        console.error("Error fetching stock time series data: ", error)
      );
  }, [selectedStock, selectedTimePeriod]);

  const parseChartData = (data) => {
    const chartData = {
      labels: [],
      datasets: [
        {
          label: `${selectedStock} Stock Price`,
          data: [],
          borderColor: "rgba(75,192,192,1)",
          fill: false,
        },
      ],
    };

    data.forEach((dayData) => {
      chartData.labels.push(dayData.date);
      chartData.datasets[0].data.push(dayData.close);
    });

    return chartData;
  };

  const options = {
    scales: {
      x: {
        type: "time",
        time: {
          unit: "day",
        },
      },
      y: {
        beginAtZero: true,
      },
    },
    responsive: true,
    maintainAspectRatio: false,
  };

  const handleAnalysisOptionClick = async (analysisType) => {
    if (analysisType === "Simple Moving Average") {
      try {
        const response = await axios.get(
          `http://localhost:3001/sma/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`Trend: ${response.data.trend}`); // Display trend
      } catch (error) {
        console.error("Error performing analysis:", error);
      }
    } else if (analysisType === "Exponential Moving Average") {
      try {
        const response = await axios.get(
          `http://localhost:3001/ema/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`EMA Trend: ${response.data.trend}`); // Display EMA trend
      } catch (error) {
        console.error("Error performing EMA analysis:", error);
      }
    } else if (analysisType === "Relative Strength Index") {
      try {
        const response = await axios.get(
          `http://localhost:3001/rsi/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`RSI Trend: ${response.data.trend}`); // Display RSI trend
      } catch (error) {
        console.error("Error performing RSI analysis:", error);
      }
    } else if (analysisType === "Bollinger Bands") {
      try {
        const response = await axios.get(
          `http://localhost:3001/bollinger-bands/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`Bollinger Bands Trend: ${response.data.trend}`); // Display Bollinger Bands trend
      } catch (error) {
        console.error("Error performing Bollinger Bands analysis:", error);
      }
    } else if (analysisType === "MACD") {
      try {
        const response = await axios.get(
          `http://localhost:3001/macd/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`MACD Trend: ${response.data.trend}`); // Display MACD trend
      } catch (error) {
        console.error("Error performing MACD analysis:", error);
      }
    } else if (analysisType === "Stochastic Oscillator") {
      try {
        const response = await axios.get(
          `http://localhost:3001/stochastic/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`Stochastic Oscillator Trend: ${response.data.trend}`); // Display Stochastic Oscillator trend
      } catch (error) {
        console.error(
          "Error performing Stochastic Oscillator analysis:",
          error
        );
      }
    } else if (analysisType === "Fibonacci Retracement") {
      try {
        const response = await axios.get(
          `http://localhost:3001/fibonacci/${selectedStock}/${selectedTimePeriod}`
        );
        alert(`Fibonacci Trend: ${response.data.trend}`); // Display trend based on Fibonacci levels
      } catch (error) {
        console.error(
          "Error performing Fibonacci Retracement analysis:",
          error
        );
      }
    }
  };

  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        marginTop: "0px",
      }}
    >
      <div style={{ width: "600px", height: "100px" }}>
        <h2 style={{ textAlign: "center" }}>{selectedStock} Stock Chart</h2>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginBottom: "10px",
          }}
        >
          {timePeriods.map((period) => (
            <button key={period} onClick={() => setSelectedTimePeriod(period)}>
              {period.toUpperCase()}
            </button>
          ))}
        </div>
        {stockData.datasets[0].data.length > 0 && (
          <Line data={stockData} options={options} />
        )}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "20px",
          }}
        >
          {analysisOptions.map((option, index) => (
            <button
              key={index}
              onClick={() => handleAnalysisOptionClick(option)}
            >
              {option}
            </button>
          ))}
        </div>
      </div>
      <div style={{ marginLeft: "20px" }}>
        <p>
          <strong>High:</strong> {stockInfo.high}
        </p>
        <p>
          <strong>Low:</strong> {stockInfo.low}
        </p>
        <p>
          <strong>Current:</strong> {stockInfo.current}
        </p>
        <p>
          <strong>Bullish: </strong> Buy Stock
        </p>
        <p>
          <strong>Bearish: </strong> Sell Stock
        </p>
        <p>
          <strong>Neutral: </strong> Keep Stock
        </p>
      </div>
    </div>
  );
};

export default Stock;
