import React, { useState, useEffect } from "react";
import axios from "axios";

const Search = ({ navigateTo, setSelectedStock }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [stockOptions, setStockOptions] = useState([]);

  useEffect(() => {
    const delayDebounceFn = setTimeout(() => {
      if (searchTerm) {
        const apiKey = "RR9BO1ZV4RXNJFJF";
        const url = `https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${searchTerm}&apikey=${apiKey}`;
  
        axios.get(url)
          .then((response) => {
            console.log("API Response:", response.data); // Log the API response
            if (response.data && response.data.bestMatches) {
              setStockOptions(response.data.bestMatches);
            } else {
              setStockOptions([]);
            }
          })
          .catch((error) => {
            console.error("Error fetching data: ", error);
            setStockOptions([]); // In case of error, clear the options
          });
      } else {
        setStockOptions([]);
      }
    }, 1000);
  
    return () => clearTimeout(delayDebounceFn);
  }, [searchTerm]);
  

  const handleStockClick = (symbol) => {
    setSelectedStock(symbol);
    navigateTo("stock");
  };

  return (
    <div>
      <form onSubmit={(e) => e.preventDefault()}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for a stock"
        />
      </form>
      {stockOptions.length > 0 && (
        <ul>
          {stockOptions.map((stock, index) => (
            <li
              key={index}
              onClick={() => handleStockClick(stock["1. symbol"])}
            >
              {stock["2. name"]} ({stock["1. symbol"]})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Search;
